{% set item_details = {"tt_gross_weight":0.0, "tt_net_weight":0, "tt_ob_qty":0.0, "tt_volume":0.0 } %}


<div class="page-break">
<div class="print-heading">
<div class="row section-break">

        <div class="col-xs-12 column-break">
    {% set u = frappe.get_doc("Company", doc.company) %}

<h3>{{ u.name or '' }}</h3>


<h5>{{u.address or ''}}
<br><b>TEL:</b>&nbsp;{{ u.phone_no or '' }} /
<b>FAX:</b>&nbsp;{{ u.fax or '' }}</h5>    


	        <h3>{{doc.name}}</h3>
	</div>
</div>

    </div>
    <div class="row section-break">
		<div class="row">
			<div class="col-xs-12 column-break">
					<center><h1>Packing list</h1></center><br>
			</div>
		</div>
		<div class="row">
			<div class="col-xs-6 column-break">
				<div class="row">
					<div class="col-xs-4 text-right">
						<label>Customer Name</label>
					</div>
					<div class="col-xs-8  value">
						{{doc.customer_name}}
					</div>
				</div>

			</div>

			<div class="col-xs-6 column-break">
				<div class="row">
					<div class="col-xs-5 text-right">
						<label>Date</label>
					</div>
					<div class="col-xs-7  value">
						{{doc.posting_date}}
					</div>
				</div>

			</div>
		</div>
	</div>
<br />
	<table class="table table-bordered">
		<tbody>
			<tr>
				<th>Sr</th>
				<th>Item Name</th>
				<th class="text-right">Qty</th>
			</tr>


			{%- for row in doc.items -%}






			<tr>
				<td style="width: 3%;">{{ row.idx }}</td>
				<td style="width: 57%;">
                                        <b>{{ row.item_code}}</b><br>
					{{ row.item_name }}

					{% set p = frappe.get_doc("Item", row.item_code ) %}

						{% if p.outer_box_code %}

						{% set ob = frappe.get_doc("Item", p.outer_box_code ) %}

					{% set total_net_weight = row.qty * p.net_weight %}
					{% if p.outer_box_pcs > 0 %}
					{% set ob_qty = row.qty / p.outer_box_pcs %}
					{% else %}
					{% set ob_qty = row.qty / 1 %}
					{% endif %}

					{% if p.inner_box_code and p.inner_box_pcs > 0 %}
					        {% set ib_qty = row.qty / p.inner_box_pcs %}
						{% set ib = frappe.get_doc("Item",  p.inner_box_code ) %}
						{% set total_gross_weight = total_net_weight + ob.net_weight*ob_qty + ib.net_weight*ib_qty %}
					{% else %}
						{% set total_gross_weight = total_net_weight + ob.net_weight*ob_qty  %}
					{% endif %}

					{% set t_volume = (ob.length * ob.width * ob.height * ob_qty)/1000000000 %} 


					<br>


					<div class="row">
					<div class="col-xs-12">
					<ul>
						<li><b>Item Net Weight: </b> {{ (p.net_weight) or ''}}</li>
						<li><b>Inner Box Pcs: </b> {{ (p.inner_box_pcs) or ''}}</li>
						<li><b>Outer Box Pcs:</b> {{ (p.outer_box_pcs) or ''}}</li>
						<li><b>Outer Box code: </b> {{ (p.outer_box_code) or ''}}</li>
						<li><b>Total Gross Weight:</b> {{ ("{:.2f}".format(total_gross_weight)) }}  </li>
						<li><b>Total Net Weight:</b>  {{ ("{:.2f}".format(total_net_weight)) }}</li>
						<li><b>Outer Box qty:</b> {{ ("{:.2f}".format(ob_qty)) }}</li>
						<li><b>Total Volume(m3):</b> {{ ("{:.2f}".format(t_volume)) }} </li>

					</ul>
					</div>
					</div>

					{% endif %}

					{% set tt_gross_weight = 0 %}
					{% set tt_net_weight = 0 %}
					{% set tt_ob_qty = 0 %}
					{% set tt_volume = 0 %}

					{% if total_gross_weight %}
					{% set tt_gross_weight = total_gross_weight + item_details["tt_gross_weight"] %} 
					{% endif %}

					{% if total_net_weight %}
					{% set tt_net_weight = total_net_weight + item_details["tt_net_weight"] %} 
					{% endif %}

					{% if ob_qty %}
					{% set tt_ob_qty = ob_qty + item_details["tt_ob_qty"] %} 
					{% endif %}

					{% if t_volume %}
					{% set tt_volume = t_volume + item_details["tt_volume"] %} 
					{% endif %}

					{% set check = item_details.update({"tt_gross_weight":tt_gross_weight, "tt_net_weight":tt_net_weight, "tt_ob_qty":tt_ob_qty, "tt_volume":tt_volume  }) %} 


				</td>

				<td style="width: 10%; text-align: right;">{{ row.qty }} {{ row.uom or row.stock_uom }}</td>
			</tr>

			{%- endfor -%}
		</tbody>
	</table>



    <div class="row section-break">
        <div class="col-xs-6 column-break">
        </div>
        <div class="col-xs-6 column-break">
                <div class="row">
					<div class="col-xs-6 text-right">
						<label>Total Gross Weight</label>
					</div>
					<div class="col-xs-6 text-right value">
						{{ ("{:.2f}".format(item_details["tt_gross_weight"])) }}
					</div>
				</div>
                <div class="row">
					<div class="col-xs-6 text-right">
						<label>Total Net Weight</label>
					</div>
					<div class="col-xs-6 text-right value">
						{{ ("{:.2f}".format(item_details["tt_net_weight"])) }}
					</div>
				</div>
				<div class="row">
					<div class="col-xs-6 text-right">
						<label>Total Box Qty</label>
					</div>
					<div class="col-xs-6 text-right value">
						{{ ("{:.2f}".format(item_details["tt_ob_qty"])) }}
					</div>
				</div>
				<div class="row">
					<div class="col-xs-6 text-right">
						<label>Total Volume</label>
					</div>
					<div class="col-xs-6 text-right value">
						{{ ("{:.2f}".format(item_details["tt_volume"])) }}
					</div>
				</div>

        </div>
    </div>
</div>
